import { TYPES } from "../actions/action-types";
import { IQuizListItem } from "../models/index";

export interface IQuizInitialSate {
	quizListItem : IQuizListItem[]
}

const quizInitialSate : IQuizInitialSate = {
	quizListItem: []
}
export const QuizReducer = (state = quizInitialSate, action: any): any=>{
switch (action.type) {
	case TYPES.getQuizListItems:
		return {
			...state,
			quizListItem : action.payload
		}
		break;
	default:
		return state
}
}
