import { TYPES } from "../actions/action-types";

export interface IQuizInitialSate {
	quizListItem : I
}
export const QuizReducer = (state = {}, action: any): any=>{
switch (action.type) {
	case TYPES.getQuizListItems:
		return {
			...state,
			quizListItem : action.payload
		}
		break;

	default:
		return state
}
}
